import React, { useState, useContext } from "react";
import { myContext } from "../../App";
import "../../styles/reportPage/Report.css";

const Report = () => {
  // useState is a hook that allows us to create a state variable and set it to a value
  // state variables for financial data
  const { setReports } = useContext(myContext);
  const [offer, setOffer] = useState(0);
  const [donation, setDonation] = useState(0);
  const [priestExpense, setPriestExpense] = useState(0);
  const [choirExpense, setChoirExpense] = useState(0);
  const [generalExpense, setGeneralExpense] = useState(0);
  const [date, setDate] = useState(null);
  // State variables report for sacrament data
  const [baptism, setBaptism] = useState("");
  const [firstCommunion, setFirstCommunion] = useState("");
  const [confirmation, setConfirmation] = useState("");
  const [covenant, setCovenant] = useState("");
  const [other, setOther] = useState("");
  const [sacramentDate, setSacramentDate] = useState(null);
  const [financeReport, setFinanceReport] = useState([]);
  const [sacramentsServicesReport, setSacramentsServicesReport] = useState([]);

  // Function to handle changes for the input fields
  const onChange = (event) => {
    switch (event.target.name) {
      case "offer":
        setOffer(event.target.value);
        break;
      case "donation":
        setDonation(event.target.value);
        break;
      case "priestExpense":
        setPriestExpense(event.target.value);
        break;
      case "choirExpense":
        setChoirExpense(event.target.value);
        break;
      case "generalExpense":
        setGeneralExpense(event.target.value);
        break;
      case "date":
        setDate(event.target.value);
        break;
      case "baptism":
        setBaptism(event.target.value);
        break;
      case "firstCommunion":
        setFirstCommunion(event.target.value);
        break;
      case "confirmation":
        setConfirmation(event.target.value);
        break;
      case "covenant":
        setCovenant(event.target.value);
        break;
      case "other":
        setOther(event.target.value);
        break;
      case "sacramentDate":
        setSacramentDate(event.target.value);
        break;
      default:
        break;
    }
  };

  // Function to handle the submit button for the financial report
  const onSubmitFi = (event) => {
    event.preventDefault();
    const newReport = {
      offer,
      donation,
      priestExpense,
      choirExpense,
      generalExpense,
      date,
    };
    setFinanceReport([...financeReport, newReport]);
    setOffer(0);
    setDonation(0);
    setPriestExpense(0);
    setChoirExpense(0);
    setGeneralExpense(0);
    setDate(null);
  };

  // Function to handle the submit button for the sacrament report
  const onSubmitSacrament = (event) => {
    event.preventDefault();
    const newReport = {
      baptism,
      firstCommunion,
      confirmation,
      covenant,
      other,
      sacramentDate,
    };
    setSacramentsServicesReport([...sacramentsServicesReport, newReport]);
    setBaptism("");
    setFirstCommunion("");
    setConfirmation("");
    setCovenant("");
    setOther("");
    setSacramentDate(null);
  };

  return (
    <div>
      <section className="report-container">
        {/* Report Title  */}
        <div className="report-title">
          <h1> Reports of Eritrean Roman Catholic Church in Hamburg </h1>
        </div>

        {/* Financial Report Form */}
        <div className="finance-form-container">
          <form action="" className="finance-form">
            <div className="offer-container">
              <label htmlFor="offer"> Offer on the Mass Service </label>
              <input
                type="number"
                name="offer"
                id="offer"
                onChange={onChange}
                value={offer}
                placeholder="Offer"
              />
            </div>

            <div className="members-donation-container">
              <label htmlFor="donation"> Monthly Members Donation </label>
              <input
                type="number"
                name="donation"
                id="donation"
                onChange={onChange}
                value={donation}
                placeholder="Members payment"
              />
            </div>

            <div className="priest-expense-container">
              <label htmlFor="priestExpense"> Mass Service Payment </label>
              <input
                type="number"
                name="priestExpense"
                id="priestExpense"
                onChange={onChange}
                value={priestExpense}
                placeholder="Mass Service Payment"
              />
            </div>

            <div className="choir-container">
              <label htmlFor="choirExpense"> Choir Expenses </label>
              <input
                type="number"
                name="choirExpense"
                id="choirExpense"
                onChange={onChange}
                value={choirExpense}
                placeholder="Choir Expenses"
              />
            </div>

            <div className="general-container">
              <label htmlFor="generalExpense"> General Expenses </label>
              <input
                type="number"
                name="generalExpense"
                id="generalExpense"
                onChange={onChange}
                value={generalExpense}
                placeholder="General Expenses"
              />
            </div>

            <div>
              <label htmlFor="date"> Service Date </label>
              <input
                type="date"
                name="date"
                id="date"
                onChange={onChange}
                value={date}
                placeholder="Service Date"
              />
            </div>
          </form>

          <button onClick={onSubmit}> Submit </button>
        </div>

        {/*  Sacrament Report Form */}
        <div className="sacrament-form-container">
          <form action="" className="sacrament-form">
            <div>
              <label htmlFor="baptism"> Baptism </label>
              <input
                type="text"
                name="baptism"
                id="baptism"
                onChange={onChange}
                value={baptism}
                placeholder="Baptism"
              />
            </div>

            <div>
              <label htmlFor="firstCommunion"> First Communion </label>
              <input
                type="text"
                name="firstCommunion"
                id="firstCommunion"
                onChange={onChange}
                value={firstCommunion}
                placeholder="First Communion"
              />
            </div>

            <div>
              <label htmlFor="confirmation"> Confirmation </label>
              <input
                type="text"
                name="confirmation"
                id="confirmation"
                onChange={onChange}
                value={confirmation}
                placeholder=" Confirmation Service"
              />
            </div>

            <div>
              <label htmlFor="covenant"> Covenant Service</label>
              <input
                type="text"
                name="covenant"
                id="covenant"
                onChange={onChange}
                value={covenant}
                placeholder="Covenant Service"
              />
            </div>

            <div>
              <label htmlFor="other"> Other Services </label>
              <input
                type="text"
                name="other"
                id="other"
                onChange={onChange}
                value={other}
                placeholder=" Other Sacrament Service"
              />
            </div>

            <div>
              <label htmlFor="date"> Service Date </label>
              <input
                type="date"
                name="date"
                id="date"
                onChange={onChange}
                value={sacramentDate}
                placeholder="Service Date"
              />
            </div>
          </form>
          <button> Submit </button>
        </div>
      </section>

      {/*  Finance and Sacrament Services Report  */}
      <div className="finance-sacrament-report-container">
        <section className="financial-report-container">
          <h2> Annual Financial Report </h2>
          <hr />
          <div className="financial-title-report-container">
            <p>Date</p>
            <p>Offer</p>
            <p>Members Donation</p>
            <p>Mass Service Payment</p>
            <p>Choir Expenses</p>
            <p>General Expenses</p>
            <p>Total</p>
          </div>
          <hr />

          {financeReport.map((report, index) => {
            return (
              <div key={index} className="financial-report">
                <p> {report.date} </p>
                <p> {report.offer} </p>
                <p> {report.donation} </p>
                <p> {report.priestExpense} </p>
                <p> {report.choirExpense} </p>
                <p> {report.generalExpense} </p>
              </div>
            );
          })}
          <div className="surplus-container">
            <h3>Surplus</h3>
          </div>
          <hr />
        </section>
        <div className="sacrament-report-container">
          <section className="sacrament-report-container">
            <h2> Annual Sacrament Services Report </h2>
            <hr />
            <div className="sacrament-title-report-container">
              <p>Date</p>
              <p>Baptism</p>
              <p>First Communion</p>
              <p>Confirmation</p>
              <p>Covenant Service</p>
              <p>Other Services</p>
            </div>
            <hr />
            {sacramentsServicesReport.map((report, index) => {
              return (
                <div key={index} className="sacrament-report">
                  <p> {report.date} </p>
                  <p> {report.baptism} </p>
                  <p> {report.firstCommunion} </p>
                  <p> {report.confirmation} </p>
                  <p> {report.covenant} </p>
                  <p> {report.other} </p>
                </div>
              );
            })}
          </section>
        </div>
      </div>
    </div>
  );
};

export default Report;
