    }
  };

  return (
    <div className="header-container">
      <nav className="navigation-bar">
        <div className="logo-container">
          <Link to="/">
            <img className="logo" src={logo} alt="Logo of LisaConsult" />
          </Link>

          <Link to="/" className="logo-text"> LisaConsult </Link>
        </div>

        <div className="menu-icon" onClick={smallScreenNavbarControl}>
          <i className={click ? "fas fa-times" : "fas fa-bars"}></i>
        </div>

        <ul className={click ? "nav-menu active" : "nav-menu"}>
          <li className="nav-item">
            <Link to="/" className="nav-links" onClick={closeMobileMenu}> Home </Link>
          </li>

          <li 
            className="nav-item" 
            onMouseEnter={onMouseEnter} 
            onMouseLeave={onMouseLeave}
          >
            <Link to="/about" className="nav-links" onClick={closeMobileMenu}> About Us <i className="fas fa-caret-down"/> </Link>
            {dropdown && <DropdownAbout/>}
          </li>

          <li 
            className="nav-item" 
            onMouseEnter={onMouseEnter} 
            onMouseLeave={onMouseLeave}
          >
            <Link to="/services" className="nav-links" onClick={closeMobileMenu}> Services <i className="fas fa-caret-down"/>  </Link>
            {dropdown && <DropdownService/>}
          </li>

          <li 
            className="nav-item" 
            onMouseEnter={onMouseEnter} 
            onMouseLeave={onMouseLeave}
          >
            <Link to="/research" className="nav-links" onClick={closeMobileMenu}> Research <i className="fas fa-caret-down"/>  </Link>
            {dropdown && <DropdownResearch/>}
          </li>

          <li className="nav-item">
            <Link to="/contact" className="nav-links" onClick={closeMobileMenu}> Contact </Link>
          </li>
        </ul>

        <div className="navbar-search-btn">
          <input type="search" />
          <label htmlFor=""> Search </label>
        </div>

      </nav>
    </div>
  );
};

export default Navigation;
