import React from "react"
import { Link } from "react-router-dom"

const MACD = new URL("../images/MACD.jpg", import.meta.url);
cons

export const technicalAnalysis = [
    {
        image: MACD,
        heading: "Moving average convergence divergence (MACD)",
        paragraph: "Moving average convergence divergence (MACD) is a trend-following momentum indicator that shows the relationship between two moving averages of a security’s price. The MACD is calculated by subtracting the 26-period exponential moving average from the 12-period EMA. The result of that calculation is the MACD line. A nine-day EMA of the MACD called the signal line, is then plotted on top of the MACD line, which can function as a trigger for buy and sell signals.",
        readMore: <Link to="/services"> Read More ...</Link>
    },

    {
        image: MACD,
        heading: "Relative Strength Index (RSI)",
        paragraph: "The Relative Strength Index (RSI) is one of the most popular and widely used momentum oscillators. The RSI measures both the speed and rate of change in price movements within the market. The values of the RSI oscillator, typically measured over a 14-day period, fluctuate between zero and 100. The Relative Strength Index indicates oversold market conditions when below 30 and overbought market conditions when above 70.",
        readMore: <Link to="/services"> Read More ...</Link>
    },

    {
        image: MACD,
        heading: "Moving averages",
        paragraph: "Moving averages are technical indicators that smooth out price action and helps to forecast future prices. The longer moving average is more smooth than the shorter moving average. This is because it averages out more prices which makes it less sensitive to new prices as they only make up a small part of the average. In addition, smoother/longer moving averages are slower to react to price movements compared to the short moving averages. ",
        readMore: <Link to="/services"> Read More ...</Link>
    }
]
