          {financeReport.map((report, index) => {
            return (
              <div key={index} className="financial-report">
                <p> Date:{report.date} </p>
                <p> Offer: {report.offer} </p>
                <p> Donation: {report.donation} </p>
                <p> Priest Expense: {report.priestExpense} </p>
                <p> Choir Expense: {report.choirExpense} </p>
                <p> General Expense: {report.generalExpense} </p>
              </div>
            );
          })}
        </section>
        <div className="sacrament-report-container"></div>
      </div>
    </div>
  );
};

export default Report;
