import React, { useState } from 'react';
import "./ContactForm.css";

const ContactForm = () => {
  const [firstName, setFirstName] = useState("");
  const [lastName, setLastName] = useState("");
  const [telephone, setTelephone] = useState("");
  const [email, setEmail] = useState("");
  const [textArea, setTextArea] = useState("");

  const updateData = (event) => {
    switch(event.target.value) {
      case "firstName":
        setFirstName(event.target.value)
        break;
      case "lastName":
        setLastName(event.target.value)
      default:
        break;
    }
  }
  return (
    <div className='contact-form-container'>
        <h2> Write us how we can serve you </h2>
        <p> Fill out the form below and we will get back to you shortly. </p>
        <form>
           <input type="text" name="firstName" value={firstName} onChange={updateData} placeholder='First Name'/>
           <input type="text" name='lastName' value={lastName} placeholder='Last Name' />
           <input type="tel" name='telephone'  placeholder='Telephone' />
           <input type="email" name="email" placeholder='Email' />
           <textarea textArea="textArea" cols="30" rows="10" placeholder='Write us message here'></textarea>
        </form>
    </div>
  )
}

export default ContactForm;
