import React, { useState, useEffect } from "react";
import { HashRouter as Router, Routes, Route } from "react-router-dom";
import Navigation from "./components/navbar/Navigation";
import About from "./views/aboutPage/About";
import Home from "./views/homePage/Home";

// a useContext hook to access the value of the context is used for the entire application
export const myContext = React.createContext();

function App() {
  // The useState hook that is used to create a state variable and set its initial value.
  const [users, setUsers] = useState([]);
  const [messages, setMessages] = useState([]);
  const [songs, setSongs] = useState([]);
  const [reports, setReports] = useState([]);

// The useEffect hook is used to run a piece of code on a specific condition.
  useEffect(() => {
    const fetchData = async () => {
      const response = await fetch("http://localhost:5000/users");
      const data = await response.json();

      try{
        if(response.ok) {
          setUsers(data);
        } else {
          throw new Error("Something went wrong");
        }
      }catch(err){
        console.log(err);
      }
    }
    fetchData();
  });


  return (
    <myContext.Provider value={{users, setUsers, messages, setMessages, songs, setSongs, reports, setReports}}>
      <div className="App">
        <Router>
          <header>
            <Navigation />
            <h1> Eritrean Roman Catholic Church in Hamburg </h1>
          </header>
          <main>
            <Routes>
              <Route path="/" element={<Home/>} />
              <Route path="/about" element={<About/>} />
              <Route path="/"  />
              <Route path="/"  />
              <Route path="/"  />
            </Routes>
          </main>
          <footer></footer>
        </Router>
      </div>
    </myContext.Provider>
  );
}

export default App;
